const params = new URLSearchParams(window.location.search);
const id = params.get("id");

const produtos = [
  {
    id: "1",
    nome: "Sushi Especial",
    imagem: "data:image/jpeg;base64,",
    descricao: "Sushi fresco com ingredientes gourmet."
  },
  {
    id: "2",
    nome: "Yakissoba",
    imagem: "data:image/jpeg;base64,",
    descricao: "Macarrão oriental com legumes e carne."
  },
  {
    id: "3",
    nome: "Temaki Salmão",
    imagem: "data:image/jpeg;base64,",
    descricao: "Cone de alga recheado com salmão."
  }
];

const produto = produtos.find(p => p.id === id);

if (produto) {
  document.getElementById("nome").innerText = produto.nome;
  document.getElementById("imagem").src = produto.imagem;
  document.getElementById("descricao").innerText = produto.descricao;
}
